sqlMap.insert:
/* setBaseData */
insert into  SEARCHBASE
              (
                  BASE_NAME
                  , BASE_CODE
              )
    VALUES  (
                #base_name#
                , #base_code#
              )

/* setNameData */
insert into SEARCHNAME(NAME_OS, NAME_NAMES, NAME_CODE)
VALUES (#name_os#, #name_names#, #name_code#)

/* setDetailData */
insert into SEARCHDETAIL(FM_URLS, DETAIL_OS, DETAIL_JOB, DETAIL_NAME, AGE, PROFILE, REG_DT)
VALUES (#fm_urls:CLOB#, #detail_os#, #detail_job:VARCHAR#, #detail_name:VARCHAR#, #age:VARCHAR#, #profile:CLOB#, SYSDATE)

/* setCareerData */
insert into SEARCHCAREER(CAREER_DT, CAREER_OS, DESCRIPTION)
VALUES (#career_dt:VARCHAR#, #career_os#, #description:CLOB#)
